import './App.css';
const api = {
  key: "ydl111Po8y1V7xflRs131Rgcis7HFf1O",
  base: "https://data.climacell.co/v4/timelines?location=13.2%2C84.2&fields=weatherCode&fields=temperature&timesteps=1h&units=metric&apikey=ydl111Po8y1V7xflRs131Rgcis7HFf1O"
}
function App() {
  console.log(new Date());
  const dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day}, ${date} ${month}, ${year}`

  }
  return (
    <div className="app warmRain">
      <main>
        <div className="location-box">
          <div className='location'> Chicago, IL</div>
          <div className='date'>{dateBuilder (new Date())}</div>
        </div>


        <input
        type="text"
        className="search"
        placeholder="Search Area"
        />
      </main>
    </div>
  );
}

export default App;
